.supermarket-pageType--default {
    .container, div[data-content-region="home_below_carousel"], [data-content-region="home_below_section2"]{
        max-width: 1440px !important;
        padding-left: 20px !important;
        padding-right: 20px !important;
    }
}

.slick-dots {
    li {
        button {
            &::before {
                background: #f98d53 !important;
            }
        }
        &.slick-active {
            button {
                &::before {
                    background: #FF6C0E !important;
                }
            }
        }
    }
}

.featured-product {
    .product-sku {
        display: none;
    }
}

.contact-us {
    .page-heading {
        display: none;
    }

    .container {
        padding: 0 !important;

        .breadcrumbs {
            max-width: 1440px !important;
            padding-left: 20px !important;
            padding-right: 20px !important;
        }

        .contact-us-container {
            .banner-section {
                margin-bottom: 10px;
            }

            .contact-us-content {
                .contact-us-content-container {
                    display: table;
                    width: 100%;

                    .contact-us-content-box {
                        padding: 60px 10px;
                        border: 1px solid #eaeaea;
                        display: table-cell;
                        width: 25%;
                        vertical-align: middle;
                        text-align: center;

                        .box-title {
                            font-size: 18px;
                            margin-top: 22px;
                            margin-bottom: 11px;
                        }

                        .box-description{
                            margin-bottom: 11px;
                            font-size: 16px;
                        }

                        .box-link{
                            color: #00ccff;
                            margin-bottom: 11px;
                            font-size: 16px;
                            font-weight: 700;
                        }

                        .box-text {
                            font-weight: 700;
                            color: #6D6E71;
                            margin-bottom: 11px;
                            font-size: 16px;
                        }

                        .box-button {
                            background: #1698d8;
                            border: 1px solid #1698d8;
                            color: #fff;
                            font-weight: 500;
                            padding: 14px 26px;
                            border-radius: 5px;
                            box-shadow: none;
                            text-transform: uppercase;
                            display: inline-block;
                            font-size: 16px;
                        }
                    }
                }
            }
        }


    }
}

