.productView {
    .option_box {
        width: 240px; 
        top: 35px;

        .productCarousel {
            margin: 0;
        }

        .product-variation-label-global {
            span {
                &:last-child {
                    display: none;
                }
            }
        }

        .productView-related {
            & > strong {
                display: none;
            }

            .product-variation-label-global {
                padding-bottom: 5px;
                display: block;
            }
        }

        .productOptionBox {
            .card-img-container:after {
                display: none;
            }

            .select-box {
                width: 100%;
                padding: 10px;
                border: 1px solid #c4c4c4;
            }

            .product-container {
                display: block;
                width: 100%;

                .product-image {
                    width: 64px;
                    height: 64px;
                    margin: 0;
                    display: inline-block;
                    vertical-align: middle;

                    .card-img-container {
                        max-width: 64px;
                        max-height: 64px;

                        .card-image {
                            display: block;
                            position: unset;
                            width: 64px;
                            height: 64px;
                            border: 1px solid #c4c4c4;
                            padding: 5px;
                            border-radius: 0;
                        }
                    }
                }

                .options-box-sku {
                    display: inline-block;
                    vertical-align: middle;
                    width: calc(100% - 102px);
                    margin-left: 5px;
                }

                .select-down-arrow {
                    display: inline-block;
                    width: 24px;
                    text-align: right;
                    border-left: 1px solid #c4c4c4;
                    height: 64px;
                    vertical-align: middle;

                    .down-arrow {
                        vertical-align: middle;
                        height: 64px;
                        display: flex;
                        align-items: center;
                        justify-content: flex-end; 
                        cursor: pointer;
                    }
                }
            }

            .dropdown-box-container {
                border-left: 1px solid #c4c4c4;
                border-right: 1px solid #c4c4c4;
                border-bottom: 1px solid #c4c4c4;
                display: block;
                width: 100%;
                float: left;
                background: #E5E5E5;
                height: 282px;
                overflow-y: scroll;
                display: none;
                padding-bottom: 30px;

                &.is-open {
                    display: block;
                    position: relative;
                    z-index: 999;
                }
            }

            .dropdown-box {
                width: 100%;
                padding: 15px;
                position: relative;
                border: 0;

                &:after {
                    content: "";
                    background: #c4c4c4;
                    position: absolute;
                    bottom: 0;
                    left: 15px;
                    height: 1px;
                    width: calc(100% - 30px);
                }

                &:last-child {
                    &:after {
                        display: none;
                    }
                }

                .product-image {
                    background: #fff;
                }

                .options-box-sku {
                    width: calc(100% - 75px);
                }
            }
        }
    }

}

