        border-radius: unset;
    }

    &:before {
        top: 22px !important;
        transform: none !important;
    }

    .qty-form-input {
        width: 46px !important;
        height: 48px !important;
        border: 0px !important;
        padding: 0 !important;
        outline-offset: -2px !important;
        font-weight: 700;
        font-size: 16px;
        text-align: center;
    }

    .buttons_box {
        width: 20px;
        display: inline-block;
        vertical-align: middle;

        .button {
            background: #fff;
            height: 24px;
            float: left;
            padding: 0;
            width: 100%;
            padding-right: 5px;
            margin: 0;

