<style>
  .organizationTypelabel:after {
    content:" *";
    color: red;
  }

  .form-checklist-checkbox ~ .form-label::before, .form-checklist-checkbox ~ .form-label::after {
        top: 0;
    }
    
    .paymentProviderHeader-img, .paymentProviderHeader-name {
        display: inline-block;
    }
    
    .form-checklist-checkbox ~ .form-label[for="radio-googlepayauthorizenet"] .paymentProviderHeader-img,  .form-checklist-checkbox ~ .form-label[for="radio-applepay"] .paymentProviderHeader-img  {
            height: 30px;
    }
    
    .checkout-form .organizationType .organizationTypelabel {
        font-weight: 700;
        font-size: 13px;
    }
    
    .checkoutRemote #applepayCheckoutButton button:hover {
        background-color: #3c4043 !important;
    }
    
    @media only screen and (max-width: 550px) { 
        .checkout-step--customer .checkoutRemote > div {
            margin-bottom: 10px;
        }
        
        .checkout-step--customer .checkoutRemote > div:last-child {
            margin-bottom: 0;
        }
        
        [data-test="payment-form"] .form-checklist-item .paymentProviderHeader-cc {
            display: none;
        }
    }
</style>

